INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'BA5895ED-EC17-4AAE-BA0D-25F2D61CDC8F', NULL, NULL, N'CGSB-43.123 8.1.4.4', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Once filled, each container shall be leak-tested. If a container shows evidence of deformation or a leakage rate greater than 2.0 x 10-3 mbar L s-1 at 20C, it shall be condemned.', N'Clause', N'CGSB-43.123 8.1.4.4', N'TDGA 5 (c)', N'TDGR 5.11', N'3122', N'Une fois rempli, chaque contenant doit tre mis  lessai pour vrifier sil fuit. Un contenant doit tre refus sil prsente une dformation ou si le taux de fuite est suprieur  2,0 x 10-3 mbar L s-1  20 C.', N'Once filled, each container shall be leak-tested. If a container shows evidence of deformation or a leakage rate greater than 2.0 x 10-3 mbar L s-1 at 20C, it shall be condemned.', N'Une fois rempli, chaque contenant doit tre mis  lessai pour vrifier sil fuit. Un contenant doit tre refus sil prsente une dformation ou si le taux de fuite est suprieur  2,0 x 10-3 mbar L s-1  20 C.', NULL)
