INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'E64E0A37-E1ED-4116-9A0A-F9FE8FC92BB8', NULL, NULL, N'CGSB-43.123 8.1.5', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'Each filled container shall be weighed. If the container shows a mass that exceeds the maximum allowable gross mass set by the manufacturer or filler, it shall be condemned. The maximum allowable gross mass shall ensure the volume of the liquid phase does not completely fill the closed container at 55C.', N'Clause', N'CGSB-43.123 8.1.5', N'TDGA 5 (c)', N'TDGR 5.11', N'3124', N'Chaque contenant rempli doit tre pes. Un contenant doit tre refus si sa masse excde la masse brute maximale permise tablie par le fabricant ou lusine de remplissage. Il faut sassurer quavec la masse brute maximale permise, le volume de la phase liquide  55 C ne remplit pas compltement le contenant ferm.', N'Each filled container shall be weighed. If the container shows a mass that exceeds the maximum allowable gross mass set by the manufacturer or filler, it shall be condemned. The maximum allowable gross mass shall ensure the volume of the liquid phase does not completely fill the closed container at 55C.', N'Chaque contenant rempli doit tre pes. Un contenant doit tre refus si sa masse excde la masse brute maximale permise tablie par le fabricant ou lusine de remplissage. Il faut sassurer quavec la masse brute maximale permise, le volume de la phase liquide  55 C ne remplit pas compltement le contenant ferm.', NULL)
GO
INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'70066A75-07A4-47D3-A365-012615305BF1', NULL, NULL, N'CGSB-43.123 8.1.6', N'CGSB-43.123 - Aerosol containers and gas cartridges for transport of dangerous goods ', NULL, N'For transport, each aerosol container shall be equipped with a means of valve protection designed and manufactured so that under normal conditions of transport, including handling, the means of valve protection remains secured and actuation of the valve is prevented.', N'Clause', N'CGSB-43.123 8.1.6', N'TDGA 5 (c)', N'TDGR 5.11', N'3125', N'Pour le transport, chaque bombe arosol doit tre dote dun dispositif de protection pour le robinet. Ce dernier doit tre conu et fabriqu de faon que, dans des conditions normales de transport et de manutention, le robinet soit protg et ne puisse pas tre actionn.', N'For transport, each aerosol container shall be equipped with a means of valve protection designed and manufactured so that under normal conditions of transport, including handling, the means of valve protection remains secured and actuation of the valve is prevented.', N'Pour le transport, chaque bombe arosol doit tre dote dun dispositif de protection pour le robinet. Ce dernier doit tre conu et fabriqu de faon que, dans des conditions normales de transport et de manutention, le robinet soit protg et ne puisse pas tre actionn.', NULL)
