INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'B480B023-16BC-4C03-BA70-AAB91A84E8F4', NULL, NULL, N'CGSB-43.145 7.3.2', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Before testing in accordance with 7.3.3, the large packaging shall be filled and a load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be 1.25 times the maximum permissible gross mass.', N'Clause', N'CGSB-43.145 7.3.2', NULL, NULL, N'3341', N'Avant de procder  lessai conformment  7.3.3, remplir le grand emballage et ajouter une charge et la rpartir galement. La masse du grand emballage rempli et de la charge doit correspondre  1,25 fois la masse brute maximale admissible du grand emballage.', N'Before testing in accordance with 7.3.3, the large packaging shall be filled and a load shall be added and evenly distributed. The mass of the filled large packaging and the load shall be 1.25 times the maximum permissible gross mass.', N'Avant de procder  lessai conformment  7.3.3, remplir le grand emballage et ajouter une charge et la rpartir galement. La masse du grand emballage rempli et de la charge doit correspondre  1,25 fois la masse brute maximale admissible du grand emballage.', NULL)
