INSERT [dbo].[SOURCE__LEGISLATION] ([(Do Not Modify) Legislation], [(Do Not Modify) Row Checksum], [(Do Not Modify) Modified On], [Name], [Legislation Source], [Parent Legislation], [English Text], [Legislation Type], [Label], [Enabling Act], [Enabling Regulation], [Order], [French Text], [Violation Display Text EN], [Violation Display Text FR], [Violation Text]) VALUES (N'0E6D3C20-C010-4E15-A328-3B8921887E99', NULL, NULL, N'CGSB-43.145 7.4.4 a)', N'CGSB-43.145 - Design, manufacture and use of large packagings for the transportation of dangerous goods, classes 3, 4, 5, 6.1, 8 and 9', NULL, N'Criteria for passing the test. Flexible large packaging: There shall be no observable permanent damage to the large packaging or its lifting devices which renders the large packaging unsafe for transport or handling and no release of the contents.', N'Clause', N'CGSB-43.145 7.4.4 a)', NULL, NULL, N'3348', N'Critres dun essai russi. Grand emballage souple : il ne doit y avoir aucun dommage au grand emballage ou aux dispositifs de levage qui rendrait le grand emballage impropre au transport ou  la manutention et il ne doit y avoir aucune perte de contenu.', N'Criteria for passing the test. Flexible large packaging: There shall be no observable permanent damage to the large packaging or its lifting devices which renders the large packaging unsafe for transport or handling and no release of the contents.', N'Critres dun essai russi. Grand emballage souple : il ne doit y avoir aucun dommage au grand emballage ou aux dispositifs de levage qui rendrait le grand emballage impropre au transport ou  la manutention et il ne doit y avoir aucune perte de contenu.', NULL)
